import { Component, OnInit, ViewChild } from '@angular/core';
import { Colors } from '../../models/colors';

@Component({
  selector: 'app-add-mark',
  templateUrl: './add-mark.component.html',
  styleUrls: ['./add-mark.component.scss']
})
export class AddMarkComponent implements OnInit {

  @ViewChild('f') form : any;
  constructor() { }
  public colors = Colors

  ngOnInit(): void {
  }


  public async addMarkSubmit(): Promise<void> {
    if (this.form.valid) {
      const title = this.form.value.title
      const description = this.form.value.description
      const color = this.form.value.color
      let tabId = ''
      this.appService.getSelectedTab().subscribe(async data => {
        tabId = data._id
      })
      await this.appService.createNewCategory(title, description,color, tabId).then(data => {
        if (data._id) {
          this.appService.updateCategories(data, 'add')
        }
      })
      console.log(this.form)
    } else {
      console.log('not valid !!!')
    }
  }
}
  <form (ngSubmit)="addMarkSubmit()" class="form-section">
    <h3 class="text-center header-title">Add New Mark</h3>
    <div class="form-group">
      <label for="mTitle">Title</label>
      <input ngModel required type="text" id="mTitle" name="title" placeholder="Title">
    </div>
    <div class="form-group">
      <label for="mDescription">description</label>
      <input ngModel required type="text" id="mDescription" name="description" placeholder="Description">
    </div>
    <div class="form-group">
      <label for="mUrl">Url</label>
      <input ngModel required type="text" id="mUrl" name="url" placeholder="https://exmple.com">
    </div>
    <div class="form-group">
      <label for="mColor">Color</label>
      <select ngModel required id="mColor" name="color" class="select">
        <option selected disabled>Select Color</option>
        <option [ngStyle]="{'color':color.value}" *ngFor="let color of colors | keyvalue" value={{color.value}}>
          {{color.key}}
        </option>
      </select>
      <!-- <input type="text" id="fname" name="fname" placeholder="Color"> -->
    </div>
    <div class="form-group align-center">
      <button class="button">Save</button>
    </div>
  </form>
