    <input required ngModel type="email" id="semail" name="email" placeholder="ex@ex.com" />
  </div>
  <div class="form-group">
    <label for="lpassword">password</label>
    <input required ngModel type="text" id="spassword" name="password" placeholder="password" />
  </div>
  <div class="form-group align-center">
    <button class="button Signup-button" (click)="signup()">
      Signup
    </button>
  </div>
</form>
