import { Component, OnInit, ViewChild } from '@angular/core';
import { AppServicesService } from 'src/app/services/app-services.service';
import { Colors } from '../../models/colors';
@Component({
  selector: 'app-add-tab',
  templateUrl: './add-tab.component.html',
  styleUrls: ['./add-tab.component.scss']
})
export class AddTabComponent implements OnInit {

  @ViewChild('f') form : any

  constructor(
    private appService : AppServicesService
  ) { }
  public colors = Colors
  public sfsdf = []
  ngOnInit(): void {
  }

  public async addNewTab() :Promise<void>{
    if(this.form.valid){
      const title = this.form.value.title
      const description = this.form.value.description
      const color = this.form.value.color
      await this.appService.createNewTab(title, description,color).then(data => {
        if(data._id){
          this.appService.updateTabs(data,'add')
        }
        console.log(data)
      })
    }else{
      console.log('form is not valid')
    }
  }
}
  <form (ngSubmit)="addNewTab()" class="form-section" #f="ngForm">
    <h3 class="text-center header-title">Add New Tab</h3>
    <div class="form-group">
      <label for="tTitle">Title</label>
      <input ngModel required type="text" id="tTitle" name="title" placeholder="Title">
    </div>
    <div class="form-group">
      <label for="tDescription">description</label>
      <input ngModel required type="text" id="tDescription" name="description" placeholder="Description">
    </div>
    <div class="form-group">
      <label for="tColor">Color</label>
      <select ngModel required id="tColor" name="color" class="select">
        <option selected disabled>Select Color</option>
        <option [ngStyle]="{'color':color.value}" *ngFor="let color of colors | keyvalue" value={{color.value}}>
          {{color.key}}
        </option>
      </select>
      <!-- <input type="text" id="fname" name="fname" placeholder="Color"> -->
    </div>
    <div class="form-group align-center">
      <button type="submit" class="button">Save</button>
    </div>
  </form>
