import { Component, OnInit, ViewChild } from '@angular/core';
import { BookMark } from '../../models/bookmarking.interface';
import { AppServicesService } from '../../services/app-services.service';
import { Colors } from '../../models/colors';

@Component({
  selector: 'app-edit-mark',
  templateUrl: './edit-mark.component.html',
  styleUrls: ['./edit-mark.component.scss']
})
export class EditMarkComponent implements OnInit {

  @ViewChild('f') form : any;
  public mark? : BookMark
  constructor(
    private appService : AppServicesService
  ) { }
  public colors = Colors

  ngOnInit(): void {
    this.appService.getSelectedMarkForEdit().subscribe(data => {
      this.mark = data;
      console.log(this.mark)
    })
  }

  public async updateMarkSubmited(): Promise<void> {
    if(this.form.valid){
      const title = this.form.value.title
      const description = this.form.value.description
      const color = this.form.value.color
      const url = this.form.value.url
      const _id = this.form.value.id
      await this.appService.updateMark(_id ,title, description,url,color).then(data => {
        if (data?._id) {
          this.appService.updateMarks(data, 'update')
        }
      })

    }else{
      console.log('not valid !!!')

    }
  }

}
<form (ngSubmit)="updateMarkSubmited()" class="form-section" #f="ngForm">
  <h3 class="text-center header-title">Edit Mark</h3>
  <div class="form-group">
    <label for="mTitle">Title</label>
    <input ngModel required type="text" id="mTitle" name="title" >
  </div>
  <div class="form-group">
    <label for="mDescription">description</label>
    <input ngModel required type="text" id="mDescription" name="description" value={{mark?.description}}>
  </div>
  <div class="form-group">
    <label for="mUrl">Url</label>
    <input ngModel required type="text" id="mUrl" name="url" value={{mark?.url}}>
  </div>
  <div class="form-group">
    <label for="mColor">Color</label>
    <select ngModel required id="mColor" name="color" class="select">
      <option [ngStyle]="{'color':color.value}" *ngFor="let color of colors | keyvalue" value={{color.value}} [selected]="mark?.color === color.value">
        {{color.key}}
      </option>
    </select>
    <!-- <input type="text" id="fname" name="fname" placeholder="Color"> -->
  </div>
  <input type="text" value={{mark?._id}} hidden name="id" />
  <div class="form-group align-center">
    <button class="button">Save</button>
  </div>
</form>
